import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default function SelectBox() {
    const [roomType, setRoomType] = React.useState('');
    const [guests, setGuests] = React.useState('');

    const handleRoomTypeChange = (event) => {
        setRoomType(event.target.value);
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        if (!roomType || !guests) {
            alert("Please select both the room type and number of guests.");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ roomCategory: "singleRoom", roomType, guests }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Error saving booking details");
            }

            const data = await response.json();
            console.log("Single Room Booking saved:", data);
            alert("Single room booking details saved successfully!");
        } catch (error) {
            console.error("Error during booking submission:", error.message);
            alert(error.message || "Failed to save booking details. Please try again.");
        }
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleRoomTypeChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value="singleKing">Single King</MenuItem>
                    <MenuItem value="singleDuplex">Single Duplex</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="guests-label">Number of Guests</InputLabel>
                <Select
                    labelId="guests-label"
                    id="guests-select"
                    value={guests}
                    label="Number of Guests"
                    onChange={handleGuestsChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>1 Guest</MenuItem>
                    <MenuItem value={2}>2 Guests</MenuItem>
                </Select>
                <FormHelperText>Choose the number of guests</FormHelperText>
            </FormControl>

            <Button onClick={handleSubmit} variant="contained" color="primary">
                Submit
            </Button>
        </Box>
    );
}
