import React, { useState } from "react";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { TextField, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, ListItemText, Grid } from "@mui/material";
import { useNavigate } from 'react-router-dom';

export default function SelectLabelsSingle() {
  const [roomType, setRoomType] = useState("");
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const [guests] = useState(1);
  const [error, setError] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [open, setOpen] = useState(false);
  const [bookings, setBookings] = useState([]);
  const navigate = useNavigate();

  const handleRoomChange = (event) => {
    setRoomType(event.target.value);
    setError(false);
  };

  const handleCheckInDateChange = (newDate) => {
    setCheckInDate(newDate);
  };

  const handleCheckOutDateChange = (newDate) => {
    setCheckOutDate(newDate);
  };

  const calculateTotalPrice = () => {
    if (checkInDate && checkOutDate) {
      const checkIn = checkInDate.toDate();
      const checkOut = checkOutDate.toDate();
      const timeDifference = checkOut - checkIn;
      const daysBooked = timeDifference / (1000 * 3600 * 24);
      return daysBooked * 400; // Price for single room
    }
    return 0;
  };

  const handleSubmit = async () => {
    if (!roomType || !checkInDate || !checkOutDate) {
      setError(true);
      alert("Please complete all fields");
      return;
    }
    const totalPrice = calculateTotalPrice();
    setTotalPrice(totalPrice);

    alert(`Total Price: ${totalPrice} EGP`);

    const userId = localStorage.getItem("userId");

    const requestBody = {
      roomType,
      checkInDate: checkInDate.toISOString(),
      checkOutDate: checkOutDate.toISOString(),
      totalPrice,
      guests,
      userId,
    };

    try {
      const response = await fetch("/bookroom", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestBody),
      });

      const result = await response.json();

      if (response.ok) {
        alert("Room booked successfully!");
        setBookings(result.bookings);
        setOpen(true);
      } else {
        alert("Failed to book room.");
      }
    } catch (error) {
      console.error("Error booking room:", error);
    }
  };

  return (
    <Box sx={{ width: "100%" }}>
      <FormControl fullWidth sx={{ marginBottom: "20px" }} error={error}>
        <InputLabel>Room Type</InputLabel>
        <Select value={roomType} onChange={handleRoomChange} label="Room Type">
          <MenuItem value="Single">Single Room</MenuItem>
          <MenuItem value="Double">Double Room</MenuItem>
        </Select>
        {error && <FormHelperText>Room Type is required</FormHelperText>}
      </FormControl>

      <Grid container spacing={2}>
        <Grid item xs={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              label="Check-In Date"
              value={checkInDate}
              onChange={handleCheckInDateChange}
              renderInput={(params) => <TextField {...params} fullWidth />}
            />
          </LocalizationProvider>
        </Grid>
        <Grid item xs={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              label="Check-Out Date"
              value={checkOutDate}
              onChange={handleCheckOutDateChange}
              renderInput={(params) => <TextField {...params} fullWidth />}
            />
          </LocalizationProvider>
        </Grid>
      </Grid>

      <Button
        sx={{ marginTop: "20px" }}
        variant="contained"
        color="primary"
        onClick={handleSubmit}
      >
        Book Now
      </Button>

      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>Booking Confirmation</DialogTitle>
        <DialogContent>
          <List>
            <ListItem>
              <ListItemText primary={`Room Type: ${roomType}`} />
            </ListItem>
            <ListItem>
              <ListItemText primary={`Total Price: ${totalPrice} EGP`} />
            </ListItem>
            <ListItem>
              <ListItemText primary={`Check-In Date: ${checkInDate && checkInDate.format("YYYY-MM-DD")}`} />
            </ListItem>
            <ListItem>
              <ListItemText primary={`Check-Out Date: ${checkOutDate && checkOutDate.format("YYYY-MM-DD")}`} />
            </ListItem>
          </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => navigate("/home")}>Go to Home</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
