import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

export default function SelectBoxServices() {
  const [foodBundle, setFoodBundle] = React.useState('');
  const [mealType, setMealType] = React.useState('');
  const [customPeriod, setCustomPeriod] = React.useState('');
  const [court, setCourt] = React.useState('');
  const [timeCourt, setTimeCourt] = React.useState('');
  const [gymTime, setGymTime] = React.useState('');
  const [dialogOpen, setDialogOpen] = React.useState(false);

  const handleFoodBundleChange = (event) => {
    const value = event.target.value;
    if (value === 'custom') {
      setDialogOpen(true);
    } else {
      setFoodBundle(value);
    }
  };

  const handleMealTypeChange = (event) => {
    setMealType(event.target.value);
  };

  const handleCourtChange = (event) => {
    setCourt(event.target.value);
  };

  const handleCourtTimeChange = (event) => {
    setTimeCourt(event.target.value);
  };

  const handleGymTimeChange = (event) => {
    setGymTime(event.target.value);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
  };

  const handleDialogSave = () => {
    if (customPeriod) {
      setFoodBundle(`Custom (${customPeriod} days)`);
    }
    setDialogOpen(false);
  };

  const calculatePrice = () => {
    const customPeriodValue = parseInt(customPeriod, 10);
    if (foodBundle === 'monthly' && mealType === 'breakfast-lunch-dinner') return 5100;
    if (foodBundle === '22-days' && mealType === 'breakfast-lunch-dinner') return 3740;
    if (foodBundle === 'monthly' && mealType === 'lunch') return 3000;
    if (foodBundle === '22-days' && mealType === 'lunch') return 3000;
    if (foodBundle.includes('Custom') && mealType === 'breakfast-lunch-dinner' && customPeriodValue)
      return customPeriodValue * 170;
    if (foodBundle.includes('Custom') && mealType === 'lunch' && customPeriodValue) return customPeriodValue * 100;
    return 0;
  };

  const handleSubmit = async () => {
    // Get booking and user data from localStorage
    const bookingId = localStorage.getItem('bookingId');
    const userId = localStorage.getItem('userId');

    // Create the booking object with services
    const bookingDetails = {
      bookingId,
      userId,
      services: {
        food: {
          bundle: foodBundle,
          customPeriod,
          mealType
        },
        court: {
          type: court,
          time: timeCourt
        },
        gym: {
          time: gymTime
        }
      }
    };

    try {
      console.log(bookingDetails);
      const response = await fetch('http://localhost:5000/api/services', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bookingDetails),
      });

      const data = await response.json();
      console.log('Booking saved:', data);
      alert('Booking saved successfully');
    } catch (error) {
      console.error('Error saving booking:', error);
    }
  };

  return (
    <div>
      {/* Food Bundle Section */}
      <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="food-bundle-select-label">Food Bundle</InputLabel>
        <Select
          labelId="food-bundle-select-label"
          id="food-bundle-select"
          value={foodBundle}
          label="Food Bundle"
          onChange={handleFoodBundleChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="monthly">Monthly (30 days)</MenuItem>
          <MenuItem value="22-days">22 Days</MenuItem>
          <MenuItem value="custom">Custom</MenuItem>
        </Select>
        <FormHelperText>Choose the food bundle</FormHelperText>
      </FormControl>

      <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="meal-type-select-label">Meal Type</InputLabel>
        <Select
          labelId="meal-type-select-label"
          id="meal-type-select"
          value={mealType}
          label="Meal Type"
          onChange={handleMealTypeChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="lunch">Lunch</MenuItem>
          <MenuItem value="breakfast-lunch-dinner">Breakfast + Lunch + Dinner</MenuItem>
        </Select>
        <FormHelperText>Select meal type</FormHelperText>
      </FormControl>

      {/* Court Section */}
      <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="court-select-label">Court Type</InputLabel>
        <Select
          labelId="court-select-label"
          id="court-select"
          value={court}
          label="Court Type"
          onChange={handleCourtChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="tennis">Tennis</MenuItem>
          <MenuItem value="basketball">Basketball</MenuItem>
          <MenuItem value="football">Football</MenuItem>
          <MenuItem value="volleyball">Volleyball</MenuItem>
        </Select>
        <FormHelperText>Choose your court</FormHelperText>
      </FormControl>

      <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="time-court-select-label">Time</InputLabel>
        <Select
          labelId="time-court-select-label"
          id="time-court-select"
          value={timeCourt}
          label="Time"
          onChange={handleCourtTimeChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="5-6">5-6</MenuItem>
          <MenuItem value="6-7">6-7</MenuItem>
          <MenuItem value="8-9">8-9</MenuItem>
        </Select>
        <FormHelperText>Choose court time</FormHelperText>
      </FormControl>

      {/* Gym Section */}
      <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="gym-time-select-label">Gym Time</InputLabel>
        <Select
          labelId="gym-time-select-label"
          id="gym-time-select"
          value={gymTime}
          label="Gym Time"
          onChange={handleGymTimeChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="5-6">5-6</MenuItem>
          <MenuItem value="6-7">6-7</MenuItem>
          <MenuItem value="8-9">8-9</MenuItem>
        </Select>
        <FormHelperText>Choose your gym time</FormHelperText>
      </FormControl>

      {/* Dialog for Custom Period */}
      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>Set Custom Period</DialogTitle>
        <DialogContent>
          <DialogContentText>Enter the custom period for the food bundle (in days):</DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="custom-period"
            label="Custom Period"
            type="number"
            fullWidth
            variant="outlined"
            value={customPeriod}
            onChange={(e) => setCustomPeriod(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose}>Cancel</Button>
          <Button onClick={handleDialogSave} disabled={!customPeriod}>
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* Price Display */}
      {foodBundle && mealType && (
        <Typography sx={{ m: 2, fontWeight: 'bold' }}>
          Total Price: {calculatePrice()} EGP
        </Typography>
      )}

      {/* Submit Button */}
      <Button variant="contained" fullWidth sx={{ marginTop: 3 }} onClick={handleSubmit}>
        Submit
      </Button>
    </div>
  );
}
