import { Box } from '@mui/material';
import React from 'react';
import SelectLabelsSingle from './components/selectBox'; // Updated to Single
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            <Box
                sx={{
                    display: 'flex',
                    flexDirection: { xs: 'column', md: 'row' }, // Responsive: stack on small screens
                    justifyContent: 'space-between',
                    alignItems: 'flex-start',
                    gap: '20px',
                }}
            >
                {/* Booking Form Section */}
                <Box
                    sx={{
                        flex: 1,
                        maxWidth: '50%',
                        padding: '20px',
                        borderRadius: '8px',
                        boxShadow: 3,
                        backgroundColor: 'white',
                    }}
                >
                    <SelectLabelsSingle />

                    <Box
                        sx={{
                            marginTop: '20px',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                        }}
                    >
                        <h3 style={{ marginBottom: '16px' }}>Deluxe Double Room</h3>
                        <p style={{ lineHeight: '1.6', marginBottom: '16px' }}>
                            Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee maker, and a toaster. The spacious double room features air conditioning, a mini-bar, a terrace with a quiet street view, and a private bathroom boasting a bath.
                        </p>
                    </Box>

                    <Box
                        sx={{
                            marginTop: '20px',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                        }}
                    >
                        <h3 style={{ marginBottom: '16px' }}>Facilities</h3>
                        <ul>
                            <li>Safe box</li>
                            <li>Cleaning services</li>
                            <li>Air conditioning</li>
                            <li>Ensuite bathroom</li>
                            <li>Soundproofing</li>
                        </ul>
                    </Box>
                </Box>

                {/* Image Section */}
                <Box
                    sx={{
                        flex: 1,
                        maxWidth: '40%',
                        boxShadow: 3,
                        borderRadius: '8px',
                        padding: '10px',
                        backgroundColor: 'white',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        height: 'auto',
                    }}
                >
                    {/* Placeholder for Image List */}
                    <Box
                        sx={{
                            width: '100%',
                            height: '100%',
                        }}
                    >
                        {/* <WovenImageList /> Uncomment when using the image list */}
                        <div style={{ textAlign: 'center', color: 'grey' }}>
                            <p>Image Section Placeholder</p>
                        </div>
                    </Box>
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
