import React, { useEffect, useState } from "react";
import { Box, Typography, CircularProgress, Grid, Paper, List, ListItem, ListItemText, Snackbar, Button } from "@mui/material";
import IconButton from "@mui/material/IconButton";
import DeleteIcon from "@mui/icons-material/Delete";
import MuiAlert from "@mui/material/Alert";

function Bookings() {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [bookings, setBookings] = useState([]);
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState("");

    useEffect(() => {
        const fetchBookings = async () => {
            const userId = localStorage.getItem("userId");

            if (!userId) {
                setError("User ID not found.");
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
                const data = await response.json();

                if (response.ok) {
                    setBookings(data.bookings);
                } else {
                    setError(data.message || "Failed to fetch bookings");
                }
            } catch (err) {
                setError("Failed to fetch bookings data.");
            } finally {
                setLoading(false);
            }
        };

        fetchBookings();
    }, []);

    const handleCancelBooking = async (bookingId) => {
        try {
            const response = await fetch(
                `http://localhost:5000/api/bookings/${bookingId}`,
                { method: "DELETE" }
            );

            if (response.ok) {
                setBookings((prevBookings) => prevBookings.filter((booking) => booking._id !== bookingId));

                setSnackbarMessage("Booking canceled successfully.");
                setOpenSnackbar(true);
            } else {
                const data = await response.json();
                setSnackbarMessage(data.message || "Failed to cancel booking.");
                setOpenSnackbar(true);
            }
        } catch (err) {
            setSnackbarMessage("Error connecting to the server. Please try again later.");
            setOpenSnackbar(true);
        }
    };

    const handleCloseSnackbar = () => {
        setOpenSnackbar(false);
    };

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6" color="error">
                    {error}
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundImage: "url('./background2.jpg')",
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                padding: 4,
            }}
        >
            <Grid container spacing={2} justifyContent="center">
                {bookings.length > 0 ? (
                    <Grid item xs={12} sm={8} md={6}>
                        <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
                                Your Bookings
                            </Typography>
                            <List>
                                {bookings.map((booking) => (
                                    <React.Fragment key={booking._id}>
                                        <IconButton
                                            edge="end"
                                            aria-label="delete"
                                            onClick={() => handleCancelBooking(booking._id)}
                                        >
                                            <DeleteIcon />
                                        </IconButton>
                                        <ListItem>
                                            <ListItemText primary="Room Type" secondary={booking.roomType} />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText
                                                primary="Check-in Date"
                                                secondary={new Date(booking.checkInDate).toLocaleDateString()}
                                            />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText
                                                primary="Check-out Date"
                                                secondary={new Date(booking.checkOutDate).toLocaleDateString()}
                                            />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText primary="Number of Guests" secondary={booking.guests} />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText primary="Room Price" secondary={`$${booking.totalPrice}`} />
                                        </ListItem>
                                    </React.Fragment>
                                ))}
                            </List>
                        </Paper>
                    </Grid>
                ) : (
                    <Typography variant="h6" align="center" color="textSecondary">
                        No bookings found.
                    </Typography>
                )}
            </Grid>

            {/* Snackbar for success/error messages */}
            <Snackbar
                open={openSnackbar}
                autoHideDuration={6000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: "top", horizontal: "center" }}
            >
                <MuiAlert
                    onClose={handleCloseSnackbar}
                    severity={snackbarMessage.includes("successfully") ? "success" : "error"}
                    sx={{ width: "100%" }}
                >
                    {snackbarMessage}
                </MuiAlert>
            </Snackbar>
        </Box>
    );
}

export default Bookings;
