import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default function SelectLabelsDouble() {
    const [roomType, setRoomType] = React.useState('');
    const [guests, setGuests] = React.useState('');
    const [error, setError] = React.useState(false);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests(''); // Reset number of guests when room type changes
        setError(false); // Reset error message when room type changes
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        // If the room type is "kingDeluxe" and no guests are selected, show error
        if (roomType === 'kingDeluxe' && !guests) {
            setError(true);
        } else {
            setError(false);
            // Handle form submission logic here
            console.log(`Room Type: ${roomType}, Number of Guests: ${guests}`);
            try {
                const response = await fetch('http://localhost:5000/api/booking', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ roomType, guests }),
                });

                const data = await response.json();

                if (!response.ok) {
                    alert(data.message || 'An error occurred');
                } else {
                    console.log('Success:', data);
                    alert('Room and guests successfully selected!');
                }
            } catch (error) {
                console.error('Error during submission:', error);
                alert('Something went wrong. Please try again later.');
            }
        }
    };
        
    

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleRoomChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value="kingDeluxe">King Deluxe Room</MenuItem>
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            {roomType === 'kingDeluxe' && (
                <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                    <InputLabel id="guests-label">Number of Guests</InputLabel>
                    <Select
                        labelId="guests-label"
                        id="guests-select"
                        value={guests}
                        label="Number of Guests"
                        onChange={handleGuestsChange}
                    >
                        <MenuItem value="">
                            <em></em>
                        </MenuItem>
                        <MenuItem value={1}>1 Guest</MenuItem>
                        <MenuItem value={2}>2 Guests</MenuItem>

                    </Select>
                    {error && <FormHelperText>Number of guests is required</FormHelperText>}
                </FormControl>
            )}

            <Button onClick={handleSubmit} variant="contained" color="primary">
                Select
            </Button>
        </Box>
    );
}
