import { Box } from '@mui/material';
import React from 'react';
import  SelectLabelsSingle  from './components/selectBox';  // Updated to Single

//import {WovenImageList} from "./components/ImageList";
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>
            {/* Select Labels */}
            <SelectLabelsSingle />

            

            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '10px',
                    marginTop: '20px',
                    height: 'auto',
                    minHeight: '200px',
                }}
            >
                <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Deluxe Double Room</h3>
                <p style={{ lineHeight: '1.6', textAlign: 'left', marginBottom: '16px' }}>
                    Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee maker, and a toaster. The spacious double room features air conditioning, a mini-bar, a terrace with a quiet street view, and a private bathroom boasting a bath.
                </p>
            </Box>

            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                    height: 'auto',
                    minHeight: '200px',
                }}
            >
                <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Facilities</h3>
                <ul style={{ textAlign: 'left' }}>
                    <li>Safe box</li>
                    <li>Cleaning services</li>
                    <li>Air conditioning</li>
                    <li>Ensuite bathroom</li>
                    <li>Soundproofing</li>
                </ul>
            </Box>

            

{/* Image Section */}
            <Box
                sx={{
                    position: 'absolute',
                    top: 250,
                    right: 70,
                    width: '40%',
                    height: '90vh',
                    margin: '10px',
                }}
            >
                <Box
                    sx={{
                        width: '50%',
                        height: '100%',
                    }}
                >
                    {/* <WovenImageListDouble /> */}
                </Box>
            </Box>

            {/* Book Activities Button */}
            {/* <Box>
                <Link to="/Bookservices">
                    <Button
                        variant="contained"
                        sx={{
                            position: 'absolute',
                            top: 1000,
                            right: 80,
                            width: '42%',
                            height: '30vh',
                            margin: '0px',
                            backgroundColor: "grey",
                            color: "white",
                            padding: "25px 20px",
                            fontSize: "18px",
                        }}
                    >
                        <h1>Book Your activities/services</h1>
                    </Button>
                </Link>
            </Box> */}
        </Box>
    );
}

export default BookNow;
