import { Box, Button } from '@mui/material';
import React, { useState } from 'react';
import MergedSelectComponent from "./components/selectBoxservices";

function Bookservices() {
  const [foodChoice, setFoodChoice] = useState('');
  const [mealType, setMealType] = useState('');
  const [customPeriod, setCustomPeriod] = useState('');
  const [courtChoice, setCourtChoice] = useState('');
  const [courtTime, setCourtTime] = useState('');
  const [gymTime, setGymTime] = useState('');

  const handleSubmit = () => {
    // Handle submit logic
    console.log({ foodChoice, mealType, customPeriod, courtChoice, courtTime, gymTime });
  };

  return (
    <Box sx={{ minHeight: "100vh", padding: "15px 30px", backgroundColor: "white", backgroundSize: "cover" }}>
      <Box sx={{ marginBottom: '20px', backgroundColor: 'red', color: 'white', padding: '1px', borderRadius: '8px' }}>
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <h1>E-Just services</h1>
        </div>
      </Box>

      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', boxShadow: 3, padding: '20px', borderRadius: '8px', backgroundColor: 'white', width: '100%', maxWidth: '600px', marginLeft: '20px', marginTop: '20px' }}>
        <h3>Book your food bundle</h3>
        <MergedSelectComponent
          type="food"
          onFoodChange={setFoodChoice}
          onCustomPeriodChange={setCustomPeriod}
          onMealTypeChange={setMealType}
        />
      </Box>

      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', boxShadow: 3, padding: '20px', borderRadius: '8px', backgroundColor: 'white', width: '100%', maxWidth: '600px', marginLeft: '20px', marginTop: '20px' }}>
        <h3>Book your court and gym time</h3>
        <MergedSelectComponent
          type="court"
          onCourtTypeChange={setCourtChoice}
          onCourtTimeChange={setCourtTime}
          onGymTimeChange={setGymTime}
        />
      </Box>

      <Box sx={{ textAlign: 'center', marginTop: '30px' }}>
        <Button onClick={handleSubmit} variant="contained" color="primary">
          Submit
        </Button>
      </Box>
    </Box>
  );
}

export default Bookservices;
