import { Box } from '@mui/material';
import React from 'react';
import SelectLabelsSingle from './components/selectBox';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
                display: 'flex',
                flexDirection: 'row', // Align elements horizontally
                gap: '20px',
            }}
        >
            {/* Left Section - Booking Table */}
            <Box
                sx={{
                    flex: 1,
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '20px',
                }}
            >
                {/* Header */}
                <Box
                    sx={{
                        marginBottom: '20px',
                        backgroundColor: 'red',
                        color: 'white',
                        padding: '1px',
                        borderRadius: '8px',
                    }}
                >
                    <div style={{ textAlign: 'center', marginTop: '20px' }}>
                        <h1>E-Just Hotel Guest House</h1>
                    </div>
                </Box>

                {/* Booking Form */}
                <SelectLabelsSingle />

                {/* Room Details */}
                <Box
                    sx={{
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                        minHeight: '200px',
                    }}
                >
                    <h3>Deluxe Double Room</h3>
                    <p>
                        Meals can be prepared in the kitchenette, which has an oven, a microwave,
                        a tea and coffee maker, and a toaster. The spacious double room features
                        air conditioning, a mini-bar, a terrace with a quiet street view, and a
                        private bathroom boasting a bath.
                    </p>
                </Box>

                {/* Facilities */}
                <Box
                    sx={{
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                        minHeight: '200px',
                    }}
                >
                    <h3>Facilities</h3>
                    <ul>
                        <li>Safe box</li>
                        <li>Cleaning services</li>
                        <li>Air conditioning</li>
                        <li>Ensuite bathroom</li>
                        <li>Soundproofing</li>
                    </ul>
                </Box>
            </Box>

            {/* Right Section - Photos */}
            <Box
                sx={{
                    flex: 1,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    boxShadow: 3,
                    borderRadius: '8px',
                    backgroundColor: 'white',
                }}
            >
                {/* Placeholder for Images */}
                <Box
                    sx={{
                        width: '80%',
                        height: '80%',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        backgroundColor: '#f0f0f0',
                        borderRadius: '8px',
                    }}
                >
                    <p>Images will go here</p>
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
